			function rgbToHex(rgbType) {
				  var rgb = rgbType.replace( /[^%,.\d]/g, "" ); 
			        // 쉼표(,)를 기준으로 분리해서, 배열에 담기. 
			        rgb = rgb.split( "," ); 
			
			  return "#" + Number(rgb[0]).toString(16) + Number(rgb[1]).toString(16) + Number(rgb[2]).toString(16);
			}
			
			
			
			
